// src/modules/counter.js
const CREATE_TODO = "CREATE_TODO";

// Action Creator
export const createTodo = (payload) => {
  return {
    type: CREATE_TODO,
    payload: payload,
  };
};

// 초기 상태값
const initialState = {
  todos: [],
};

// 리듀서
const todos = (state = initialState, action) => {
  switch (action.type) {
    case CREATE_TODO:
      return [...state, action.payload];
    default:
      return state;
  }
};

// 모듈파일에서는 리듀서를 export default 한다.
export default todos;
