import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import "./App.css";
import "./component/Header";
import Header from "./component/Header";
import List from "./component/List";
import { createTodo, deleteTodo, doneTodo } from "./redux/modules/todo";
import { v4 as uuidv4 } from "uuid";

const App = () => {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const todos = useSelector((state) => state.todos);
  console.log(todos);

  const dispatch = useDispatch();

  const toDo = {
    id: uuidv4(),
    title: title,
    body: body,
    isDone: false,
  };

  return (
    <div className="app">
      <div>
        <Header />
      </div>
      <div>
        <div className="create">
          <div className="input-group">
            <label htmlFor="title">제목</label>
            <input
              className="input"
              id="title"
              placeholder="제목을 입력하세요"
              value={title}
              onChange={(event) => setTitle(event.target.value)}
            ></input>
            <label htmlFor="todo">내용</label>
            <input
              className="input"
              id="todo"
              placeholder="내용을 입력하세요"
              value={body}
              onChange={(event) => setBody(event.target.value)}
            ></input>
          </div>
          <button
            className="add_btn"
            onClick={() => {
              dispatch(createTodo(toDo));
              setTitle("");
              setBody("");
            }}
          >
            추가하기
          </button>
        </div>
      </div>
      <div className="Todo">
        <div className="working">
          <h1>Working...🔥</h1>
          <div>
            {todos.map((todo) => {
              if (!todo.isDone) {
                return (
                  <div key={todo.id}>
                    <button>상세보기</button>
                    <div>{todo.title}</div>
                    <div>{todo.body}</div>
                    <div>
                      <button
                        onClick={() => {
                          dispatch(deleteTodo(todo.id));
                        }}
                      >
                        삭제하기
                      </button>
                      <button
                        onClick={() => {
                          dispatch(doneTodo(todo.id));
                        }}
                      >
                        완료
                      </button>
                    </div>
                  </div>
                );
              } else {
                return null;
              }
            })}
          </div>
        </div>
        <div className="done">
          <h1>Done! 🎉</h1>
          <div>
            {todos.map((todo) => {
              if (todo.isDone === true) {
                return (
                  <div key={todo.id}>
                    <button>상세보기</button>
                    <div>{todo.title}</div>
                    <div>{todo.body}</div>
                    <div>
                      <button
                        onClick={() => {
                          dispatch(deleteTodo(todo.id));
                        }}
                      >
                        삭제하기
                      </button>
                      <button
                        onClick={() => {
                          dispatch(doneTodo(todo.id));
                        }}
                      >
                        {todo.isDone ? "취소" : "완료"}
                      </button>
                    </div>
                  </div>
                );
              } else {
                return null;
              }
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
