import React, { useState } from "react";

const List = (props) => {
  <div>
    <div>{props.list.title}</div>
    <div>{props.list.todo}</div>
  </div>;
};

const App = () => {
  const list = [
    {
      id: 0,
      title: "",
      todo: "",
      isDone: false,
    },
    {
      id: 1,
      title: "테스트용",
      todo: "테스트용",
      isDone: false,
    },
  ];

  const [title, setTitle] = useState("");
  const [todo, setTodo] = useState("");

  return (
    <div className="App">
      <div className="top">
        <span>My Todo List</span>
        <span>React</span>
      </div>
      <div>
        {list.map((list) => {
          return <List list={list} key={list.id}></List>;
        })}
      </div>
      <div>
        <label htmlFor="title">제목</label>
        <input
          id="title"
          placeholder="제목을 입력하세요"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        ></input>
        <label htmlFor="todo"></label>
        <input
          id="todo"
          placeholder="내용을 입력하세요"
          value={todo}
          onChange={(event) => setTodo(event.target.value)}
        ></input>
        <button>추가하기</button>
      </div>
    </div>
  );
};

export default App;
