import React, { useState } from "react";
import List from "./component/List/List";
import Header from "./component/Header/Header";
import Todo from "./component/Todo/Todo";
import "./App.css";

const App = () => {
  const [lists, setLists] = useState([]);

  const deleteList = (id) => {
    const deleteTodoList = lists.filter((list) => list.id !== id);
    setLists(deleteTodoList);
  };

  const doneList = (id) => {
    const doneTodoList = lists.map((list) => {
      if (list.id === id) {
        list.isDone = !list.isDone;
        return list;
      } else {
        return list;
      }
    });
    setLists(doneTodoList);
  };

  return (
    <div className="app">
      <div>
        <Header />
      </div>
      <div>
        <Todo />
      </div>
      <div className="working">
        <h3>Working</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === false) {
              return (
                <List
                  setList={setLists}
                  delete={deleteList}
                  done={doneList}
                  list={list}
                  key={list.id}
                ></List>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
      <div className="done">
        <h3>Done</h3>
        {lists.map((list) => {
          if (list.isDone === true) {
            return (
              <List
                setList={setLists}
                delete={deleteList}
                done={doneList}
                list={list}
                key={list.id}
              ></List>
            );
          } else {
            return null;
          }
        })}
        <div></div>
      </div>
    </div>
  );
};

export default App;
