import React, { useState } from "react";
import "./Todo.css";

const Todo = (props) => {
  const addListHandler = () => {
    const newList = {
      id: props.lists.length + 1,
      title: props.title,
      todo: props.todo,
      isDone: false,
    };
    props.setLists([...props.lists, newList]);
  };

  /*const [title, setTitle] = useState("");
  const [todo, setTodo] = useState("");*/

  return (
    <div className="todo">
      <div className="input-group">
        <label className="label" htmlFor="title">
          제목
        </label>
        <input
          className="input"
          id="title"
          placeholder="제목을 입력하세요"
          value={props.list.title}
          onChange={(event) => title(event.target.value)}
        ></input>
        <label className="label" htmlFor="todo">
          내용
        </label>
        <input
          className="input"
          id="todo"
          placeholder="내용을 입력하세요"
          value={props.list.todo}
          onChange={(event) => todo(event.target.value)}
        ></input>
      </div>
      <button className="add_btn" onClick={addListHandler}>
        추가하기
      </button>
    </div>
  );
};

export default Todo;
