import React, { useState } from "react";
import List from "./component/List";
import Header from "./component/Header";
import Form from "./component/Form";

const App = () => {
  const deleteList = (id) => {
    const deleteTodoList = lists.filter((list) => list.id !== id);
    setLists(deleteTodoList);
  };

  const doneList = (isDone) => {
    const doneTodoList = lists.filter((list) => list.isDone !== isDone);
    setLists([...lists, doneTodoList]);
  };

  return (
    <div className="App">
      <div>
        <Header />
      </div>
      <div>
        <Form />
      </div>
      <div>
        <h3>Working</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === false) {
              return (
                <List
                  setList={setLists}
                  delete={deleteList}
                  done={doneList}
                  list={list}
                  key={list.id}
                ></List>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
      <div>
        <h3>Done</h3>
        {lists.map((list) => {
          if (list.isDone === true) {
            return (
              <List
                setList={setLists}
                delete={deleteList}
                done={doneList}
                list={list}
                key={list.id}
              ></List>
            );
          } else {
            return null;
          }
        })}
        <div></div>
      </div>
    </div>
  );
};

export default App;
