import React, { useState } from "react";

const List = (props) => {
  return (
    <div>
      <div>{props.list.title} 제목</div>
      <div>{props.list.todo} 내용</div>
      <div>{props.list.isDone}</div>
      <button
        onClick={() => {
          props.delete(props.list.id);
        }}
      >
        삭제하기
      </button>
      <button
        onClick={() => {
          props.done(props.list.isDone);
        }}
      >
        완료
      </button>
    </div>
  );
};

const App = () => {
  const [lists, setLists] = useState([
    {
      id: 0,
      title: "테스트",
      todo: "테스트",
      isDone: false,
    },
  ]);

  const [title, setTitle] = useState("");
  const [todo, setTodo] = useState("");

  const addListHandler = () => {
    const newList = {
      id: lists.length + 1,
      title: title,
      todo: todo,
      isDone: false,
    };
    setLists([...lists, newList]);
  };

  const deleteList = (id) => {
    const deleteTodoList = lists.filter((list) => list.id !== id);
    setLists(deleteTodoList);
  };

  const doneList = (isDone) => {
    const doneTodoList = lists.filter((list) => list.isDone === isDone);
    setLists(doneTodoList);
  };

  const cancelList = (isDone) => {
    const cancelTodoList = lists.filter((list) => list.isDone == isDone);
    setLists(cancelTodoList);
  };

  return (
    <div className="App">
      <div className="top">
        <span>My Todo List</span>
        <span>React</span>
      </div>
      <div>
        <label htmlFor="title">제목</label>
        <input
          id="title"
          placeholder="제목을 입력하세요"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        ></input>
        <label htmlFor="todo"></label>
        <input
          id="todo"
          placeholder="내용을 입력하세요"
          value={todo}
          onChange={(event) => setTodo(event.target.value)}
        ></input>
        <button onClick={addListHandler}>추가하기</button>
      </div>
      <div>
        <h3>Working</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === false) {
              return (
                <List
                  delete={deleteList}
                  cancel={cancelList}
                  list={list}
                  key={list.id}
                ></List>
              );
            }
          })}
        </div>
      </div>
      <div>
        <h3>Done</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === true) {
              return (
                <List
                  delete={deleteList}
                  done={doneList}
                  list={list}
                  key={list.id}
                ></List>
              );
            }
          })}
        </div>
      </div>
    </div>
  );
};

export default App;
