import React, { useState } from "react";
import Header from "./component/Header/Header";
import Todo from "./component/Todo/Todo";
import "./App.css";

const List = (props) => {
  return (
    <div>
      <div className="body">
        <div>{props.list.title}</div>
        <div>{props.list.todo}</div>
      </div>
      <button
        className="btn"
        onClick={() => {
          console.log("삭제");
          props.delete(props.list.id);
        }}
      >
        삭제하기
      </button>
      <button
        className="btn"
        onClick={() => {
          props.done(props.list.id);
        }}
      >
        {props.list.isDone ? "취소" : "완료"}
      </button>
    </div>
  );
};

const App = () => {
  const [lists, setLists] = useState([
    {
      id: 0,
      title: "테스트",
      todo: "테스트",
      isDone: false,
    },
  ]);

  const deleteList = (id) => {
    const deleteTodoList = lists.filter((list) => list.id !== id);
    setLists(deleteTodoList);
  };

  const doneList = (id) => {
    const doneTodoList = lists.map((list) => {
      if (list.id === id) {
        list.isDone = !list.isDone;
        return list;
      } else {
        return list;
      }
    });
    setLists(doneTodoList);
  };

  return (
    <div className="app">
      <div>
        <Header />
      </div>
      <div>
        <Todo />
      </div>
      <div className="working">
        <h3>Working</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === false) {
              return (
                <List
                  setList={setLists}
                  delete={deleteList}
                  done={doneList}
                  list={list}
                  key={list.id}
                ></List>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
      <div className="done">
        <h3>Done</h3>
        {lists.map((list) => {
          if (list.isDone === true) {
            return (
              <List
                setList={setLists}
                delete={deleteList}
                done={doneList}
                list={list}
                key={list.id}
              ></List>
            );
          } else {
            return null;
          }
        })}
        <div></div>
      </div>
    </div>
  );
};

export default App;
