import React from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";

const StDetailContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
`;

const StDetailList = styled.div`
  border: 3px solid rgb(120, 112, 112);
  border-radius: 10px;
  padding: 20px;
  line-height: 50px;
  width: 500px;
`;

function Work() {
  const todos = useSelector((state) => state.todos);
  const param = useParams();
  return (
    <StDetailContainer>
      {todos.map((item) => {
        if (item.id === param.id) {
          return (
            <StDetailList key={item.id}>
              <Link to={`/`}>이전으로</Link>
              <div>{item.id}</div>
              <div>{item.title}</div>
              <div>{item.body}</div>
            </StDetailList>
          );
        }
      })}
    </StDetailContainer>
  );
}

export default Work;
