import React, { useState } from "react";

const CustomBtn = () => {
  <button></button>;
};

const ToDo = (props) => {
  return (
    <div>
      {props.list.title} {props.list.todo}
      <CustomBtn
        color="red"
        onClick={() => {
          props.delete(props.list.id);
        }}
      >
        삭제하기
      </CustomBtn>
    </div>
  );
};

const App = () => {
  const [list, setList] = useState([
    { id: 0, title: "", todo: "", isDone: false },
  ]);

  const [title, setTitle] = useState("");
  const [todo, setTodo] = useState("");

  return (
    <div className="App">
      <div className="top">
        <span>My Todo List</span>
        <span>React</span>
      </div>
      <div>
        <label htmlFor="title">제목</label>
        <input id="title" placeholder="제목을 입력하세요">
          {title}
        </input>
        <label htmlFor="todo"></label>
        <input id="todo" placeholder="내용을 입력하세요">
          {todo}
        </input>
      </div>
      <div>
        {users.map((list) => {
          return <Todo list={todo} key={list.id} />;
        })}
      </div>
    </div>
  );
};

export default App;
