import React from "react";
import { useParams } from "react-router-dom";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";

const StDetailContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
`;

const StDetailList = styled.div`
  border: 1px solid rgb(120, 112, 112);
  border-radius: 10px;
  padding: 40px;
  line-height: 60px;
  width: 500px;
  height: 300px;
`;

const StDetailHeader = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

function Work() {
  const todos = useSelector((state) => state.todos);
  const param = useParams();
  return (
    <StDetailContainer>
      {todos.map((item) => {
        if (item.id === param.id) {
          return (
            <StDetailList>
              <StDetailHeader>
                <div>ID: ( {item.id} ) </div>
                <div key={item.id}>
                  <Link
                    to={`/`}
                    style={{
                      textDecoration: "none",
                      color: "#372e2e",
                      border: "1px solid #5a5757",
                      borderRadius: "5px",
                      padding: "10px",
                    }}
                  >
                    이전으로
                  </Link>
                </div>
              </StDetailHeader>
              <div style={{ fontSize: "1.5rem" }}>{item.title}</div>
              <div>{item.body}</div>
            </StDetailList>
          );
        }
      })}
    </StDetailContainer>
  );
}

export default Work;
