import React from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { deleteTodo, doneTodo } from "../../redux/modules/todo";
import styled from "styled-components";

const StTodoList = styled.div`
  display: flex;
  flex-direction: column;
`;

const StWorkingTodo = styled.div`
  border: 3px solid rgb(120, 112, 112);
  border-radius: 10px;
  width: 300px;
  padding: 20px;
`;

function List() {
  const todos = useSelector((state) => state.todos);

  const dispatch = useDispatch();

  return (
    <StTodoList>
      <div className="working">
        <h1>Working...🔥</h1>
        <div>
          {todos.map((todo) => {
            if (!todo.isDone) {
              return (
                <StWorkingTodo key={todo.id}>
                  <Link to={`/${todo.id}`}>상세보기</Link>
                  <div>{todo.title}</div>
                  <div>{todo.body}</div>
                  <div>
                    <button
                      onClick={() => {
                        dispatch(deleteTodo(todo.id));
                      }}
                    >
                      삭제하기
                    </button>
                    <button
                      onClick={() => {
                        dispatch(doneTodo(todo.id));
                      }}
                    >
                      완료
                    </button>
                  </div>
                </StWorkingTodo>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
      <div className="done">
        <h1>Done! 🎉</h1>
        <div>
          {todos.map((todo) => {
            if (todo.isDone === true) {
              return (
                <div key={todo.id}>
                  <Link to={`/${todo.id}`}>상세보기</Link>
                  <div>{todo.title}</div>
                  <div>{todo.body}</div>
                  <div>
                    <button
                      onClick={() => {
                        dispatch(deleteTodo(todo.id));
                      }}
                    >
                      삭제하기
                    </button>
                    <button
                      onClick={() => {
                        dispatch(doneTodo(todo.id));
                      }}
                    >
                      {todo.isDone ? "취소" : "완료"}
                    </button>
                  </div>
                </div>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
    </StTodoList>
  );
}
export default List;
