import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { createTodo } from "../../redux/modules/todo";
import { v4 as uuidv4 } from "uuid";
import styled from "styled-components";

const StForm = styled.form`
  border: none;
  padding: 50px;
  background-color: rgb(199, 220, 220);
  border-radius: 15px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  gap: 100px;
`;

function AddForm() {
  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const dispatch = useDispatch();

  const toDo = (event) => {
    event.preventDefault();
    if (title === "" && body === "") {
      return alert("제목과 타이틀을 전부 입력하세요.");
    } else {
      const newTodo = { id: uuidv4(), title: title, body: body, isDone: false };
      dispatch(createTodo(newTodo));
      setTitle("");
      setBody("");
    }
  };

  return (
    <StForm>
      <div className="input-group">
        <label htmlFor="title">제목</label>
        <input
          className="input"
          id="title"
          placeholder="제목을 입력하세요"
          value={title}
          onChange={(event) => setTitle(event.target.value)}
        ></input>
        <label htmlFor="todo">내용</label>
        <input
          className="input"
          id="todo"
          placeholder="내용을 입력하세요"
          value={body}
          onChange={(event) => setBody(event.target.value)}
        ></input>
      </div>
      <button className="add_btn" onClick={toDo}>
        추가하기
      </button>
    </StForm>
  );
}

export default AddForm;
