import React, { useState } from "react";
import List from "./component/List";
import Header from "./component/Header";
import Todo from "./component/Todo";

const App = () => {
  const [lists, setLists] = useState([
    {
      id: 0,
      title: "테스트",
      todo: "테스트",
      isDone: false,
    },
  ]);

  const [title, setTitle] = useState("");
  const [todo, setTodo] = useState("");

  const addListHandler = () => {
    const newList = {
      id: lists.length + 1,
      title: title,
      todo: todo,
      isDone: false,
    };
    setLists([...lists, newList]);
  };

  const deleteList = (id) => {
    const deleteTodoList = lists.filter((list) => list.id !== id);
    setLists(deleteTodoList);
  };

  const doneList = (id) => {
    const doneTodoList = lists.map((list) => {
      if (list.id === id) {
        list.isDone = !list.isDone;
        return list;
      } else {
        return list;
      }
    });
    setLists(doneTodoList);
  };

  return (
    <div className="App">
      <div>
        <Header />
      </div>
      <div>
        <Todo />
      </div>
      <div>
        <h3>Working</h3>
        <div>
          {lists.map((list) => {
            if (list.isDone === false) {
              return (
                <List
                  setList={setLists}
                  delete={deleteList}
                  done={doneList}
                  list={list}
                  key={list.id}
                ></List>
              );
            } else {
              return null;
            }
          })}
        </div>
      </div>
      <div>
        <h3>Done</h3>
        {lists.map((list) => {
          if (list.isDone === true) {
            return (
              <List
                setList={setLists}
                delete={deleteList}
                done={doneList}
                list={list}
                key={list.id}
              ></List>
            );
          } else {
            return null;
          }
        })}
        <div></div>
      </div>
    </div>
  );
};

export default App;
